import React, { useEffect, useState } from "react";
import searchIson from "../../images/searchIcon.svg";
import Styles from "./CitySelector.module.scss";
import cross from "../../images/cross.svg";

const CitySelector = () => {
  const [userInput, setUserInput] = useState("");
  const [cities, setCities] = useState([
    "Aberdeen",
    "Abilene",
    "Akron",
    "Albany",
    "Albuquerque",
    "Alexandria",
    "Allentown",
    "Amarillo",
    "Anaheim",
    "Anchorage",
    "Ann Arbor",
    "Antioch",
    "Apple Valley",
    "Appleton",
    "Arlington",
    "Arvada",
    "Asheville",
    "Athens",
    "Atlanta",
    "Atlantic City",
    "Augusta",
    "Aurora",
    "Austin",
    "Bakersfield",
    "Baltimore",
    "Barnstable",
    "Baton Rouge",
    "Beaumont",
    "Bel Air",
    "Bellevue",
    "Berkeley",
  ]);
  const [filteredCities, setFilteredCities] = useState([...cities]);
  useEffect(() => {
    setFilteredCities([
      ...cities.filter((city) => {
        console.log(city.startsWith(userInput));
        city.startsWith(userInput);
      }),
    ]);
  }, [userInput]);
  useEffect(() => {
    console.log(filteredCities);
  }, [filteredCities]);

  return (
    <div className={Styles.main}>
      <div className={Styles.headerBox}>
        <p className={Styles.heading}>Ваш город</p>
        <img className={Styles.cross} src={cross} alt="" />
      </div>
      <div className={Styles.inputContainer}>
        <div className={Styles.inputBox}>
          <img src={searchIson} alt="поиск" />
          <input
            type="text"
            placeholder="Найти город"
            onChange={(e) => setUserInput(e.target.value)}
          />
        </div>
      </div>
      <div className={Styles.commonCitiesBox}>
        <div className={Styles.commonCity}>
          {filteredCities.map((city, index) => (
            <p key={index}>{city}</p>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CitySelector;
