import axios from "axios";
import { useDropDownMessageContext } from "../ContextProviders/DropDownMessageProvider";

export const sendPhotoAttachment = (photos, props, setLoading) => {
  setLoading(true);
  const { message, isShown } = useDropDownMessageContext();

  const data = new FormData();

  Object.keys(photos).forEach((key) => {
    const item = photos[key];
    data.append(item.name, item);
  });
  Object.keys(props).forEach((key) => {
    const item = props[key];
    data.append(`${key}`, item);
  });

  axios
    .post(`http://127.0.0.1:4000/api/v1/forms/attachPhoto`, data, {
      withCredentials: true,
    })
    .then((res) => {
      console.log("success");
    })
    .catch((err) => {
      console.log(err);
    })
    .finally(() => {
      setLoading(false);
    });
};
