import React from "react";
import SwipingGallery from "../Swiper/Swiper";
import { Heading } from "../../elements/heading/heading";
import Styles from "./Porfolio.module.scss";
import { useWidthContext } from "../../ContextProviders/WidthProivder";

const Portfolio = () => {
  const { width } = useWidthContext();

  return (
    <div className={Styles.container}>
      <Heading
        name="Портфолио"
        extraStyles={{ margin: "0 auto", marginBottom: "47px" }}
      />
      <SwipingGallery
        slidesPerView={width <= 1030 ? 1 : 2}
        arrowSize={"70px"}
        slideDimentions={
          width <= 1030
            ? {
                height: "500px",
                width: "353px",
              }
            : {
                height: "611px",
                width: "431px",
              }
        }
        arrowPosition={"100px"}
        swiperWidth={"auto"}
      />
      {/* <SwipingGallery
        slidesPerView={width <= 1030 ? 2 : 3}
        arrowSize={"46px"}
        slideDimentions={
          width <= 1030
            ? {
                height: "151px",
                width: "370px",
              }
            : {
                height: "162px",
                width: "398px",
              }
        }
        arrowPosition={"-15px"}
      /> */}
    </div>
  );
};

export default Portfolio;
