export const sendPhotoAttachment = (photos) => {
  const attachedPhotos = new FormData();

  Object.keys(photos).forEach((key) => {
    const item = photos.item(key);
    attachedPhotos.append(item.name, item);
  });

  axios
    .post(`forms/attachPhoto`, attachedPhotos, { withCredentials: true })
    .then((res) => {
      socket.emit("fm", {
        messageId: res.data.messageId,
      });
    })
    .catch((err) => {
      setErrorMessage(Errors.default);
      setErrorPopUpIsOpen(true);
    });
};
