import {
  createContext,
  Dispatch,
  SetStateAction,
  useContext,
  useEffect,
  useState,
} from "react";

const WidthContext = (createContext < WidthProviderContext) | (null > null);
export const useWidthContext = () => {
  const currentWidthContext = useContext(WidthContext);

  if (!currentWidthContext) {
    throw new Error("WidthContext is null!");
  }

  return currentWidthContext;
};

const WidthProvider = ({ children }) => {
  const [width, setWidth] = useState("");

  useEffect(() => {
    const calculateWidth = () => {
      console.log(window.innerWidth);
      setWidth(window.innerWidth);
    };
    window.addEventListener("resize", () => {
      calculateWidth();
    });
    return () => {
      window.removeEventListener("resize", calculateWidth);
    };
  }, []);

  return (
    <WidthContext.Provider
      value={{
        width,
      }}
    >
      {children}
    </WidthContext.Provider>
  );
};

export default WidthProvider;
